import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Hero() {
  const [backgroundImage, setBackgroundImage] = useState(null);

  useEffect(() => {
    // Fetch random background image from API
    const fetchBackgroundImage = async () => {
      try {
        const response = await fetch('/api/random-background');
        const data = await response.json();
        if (response.ok) {
          setBackgroundImage(data.url);  // Set the background URL
        } else {
          console.error("Failed to fetch background image:", data.error);
        }
      } catch (error) {
        console.error("Error fetching background image:", error);
      }
    };

    fetchBackgroundImage();
  }, []);

  if (!backgroundImage) {
    return <div>Loading...</div>; // Show loading while fetching
  }

  return (
    <section
      className="hero"
      style={{
        background: `url(${backgroundImage}) no-repeat center center/cover`,
      }}
    >
      <div className="hero-content">
        <div className="h11">
          <h0>Encuentra Sorpresas Únicas Cada Mes</h0>
        </div>
        <div className="bubble">
          <p>Únete a Fan Treasure y recibe una caja misteriosa llena de productos exclusivos de tus franquicias geek y anime favoritas.</p>
        </div>
        <Link to="Subscription" className="cta-button">Suscríbete Ahora</Link>
      </div>
    </section>
  );
}

export default Hero;
