import React, { useState, useEffect } from 'react';
import './login.css';
import axios from 'axios';

function LoginPage() {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    document.title = userData ? `Welcome, ${userData.email}!` : 'LootBox';
  }, [userData]);

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/login', { username });
      //console.log('Response:', response.data.user.name); // Log the response data to the console
      setUserData(response.data);
      setErrorMessage('');

      //navigate('/home', { state: { userData: response.data } });
    } catch (error) {
      console.error('Error during login:', error);
      if (error.response && error.response.status === 401) {
        setErrorMessage('Incorrect username. Please try again.');
        setUserData(null);
      } else {
        setErrorMessage('An error occurred during login. Please try again later.');
      }
    }
  };
}

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Usuario:', username);
    console.log('Contraseña:', password);
    // Aquí puedes agregar la lógica para autenticar al usuario
  };

  return (
    <div className="login-container">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="username">Nombre de usuario</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        
        <label htmlFor="password">Contraseña</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        
        <button type="submit">Iniciar Sesión</button>
      </form>
    </div>
  );
}

export default Login;
