import React, { useState, useEffect } from 'react';
import './login.css';
import axios from 'axios';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userData, setUserData] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    document.title = userData ? `Welcome, ${userData.email}!` : 'LootBox';
  }, [userData]);

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      // Send POST request with email and password
      const response = await axios.post('/api/login', { email, password });
      
      // If successful, store user data and reset error message
      setUserData(response.data);
      setErrorMessage('');
      console.log('User Data:', response.data);

      // Redirect to home page (this can be done using React Router if necessary)
      // navigate('/home', { state: { userData: response.data } });
    } catch (error) {
      console.error('Error during login:', error);
      
      if (error.response && error.response.status === 401) {
        // Invalid credentials
        setErrorMessage('Incorrect email or password. Please try again.');
        setUserData(null);
      } else {
        // General error
        setErrorMessage('An error occurred during login. Please try again later.');
      }
    }
  };

  return (
    <div className="login-container">
      <h2>Iniciar Sesi칩n</h2>
      <form onSubmit={handleLogin}>
        <label htmlFor="email">Correo Electr칩nico</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        
        <label htmlFor="password">Contrase침a</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        
        <button type="submit">Iniciar Sesi칩n</button>
      </form>

      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
}

export default LoginPage;
